insert into educationinfo( endtime,specialtyid,school,starttime,educationbg,specialtyRemark,activeflag,studyabroad,resumeid,educationid,createby,createdate) values('2013-8','贸易学','韩国成均馆大学','2011-9','硕士','韩国名校成均馆大学是一所拥有六百多年辉煌历史的知名学府。作为韩国最具影响力的大学之一，2012年度ARWU世界大学排名韩国第三，仅次于首尔大学及KAIST。该校的贸易学专业更是公认的韩国所有大学同类专业中的NO.1。作为进入全球大学100强的重要手段之一，贸易学专业一直是成均馆大学经济学院重点发展的学科。该专业致力于培养全球化时代的精英人才，主要研究国际贸易理论与政策、企业国际化经营、国际商务与全球营销、国际贸易风险分析与规避，为企业高层管理人员制定国际化经营战略和政府有关部门制定国际贸易政策提供理论依据和分析手段。 ','1','',6800441,'6800443','sysadmin',now());
insert into educationinfo( endtime,specialtyid,school,starttime,educationbg,specialtyRemark,activeflag,studyabroad,resumeid,educationid,createby,createdate) values('2011-8','国际贸易','韩国釜山外国语大学','2006-8','本科','国际贸易学专业属于经济学学科范畴，主要以经济学理论为依托，包括微观经济学、宏观经济学、国际经济学、计量经济学、世界经济学概论等。但是在学习这些经济学之前，要掌握相应的基础知识，比如，会计学原理、统计学、财政学、货币银行学、经济法、国际商法、证券投资、财务管理、管理学原理等课程。在学习完上述课程之后，就要接触到国际贸易最核心的知识了，就是国际贸易各个方面的专业知识，即：国际贸易理论、国际金融、国际贸易结算、国际市场营销、贸易英语、国际贸易保险、国际经济合作、跨国公司理论与实务等，当然最重要的课程为——国际贸易实务，也称作进出口贸易实务，或对外贸易实务。','1','',6800441,'6800444','sysadmin',now());
